function removeActiveButton(){var t=document.querySelectorAll("[data-tab-button]");for(i=0;i<t.length;i++)t[i].parentElement.classList.remove("navigation__list__item--active")}function hideAll(){var e=document.querySelectorAll("[data-tab-id]");for(let t=0;t<e.length;t++)e[t].classList.add("tab")}$(document).ready(function(){var e=document.querySelectorAll("[data-tab-button]");for(let t=0;t<e.length;t++)e[t].addEventListener("click",function(t){var e=t.target.dataset.tabButton,t=(removeActiveButton(),t.target.parentElement.classList.add("navigation__list__item--active"),document.querySelector(`[data-tab-id = ${e}]`));hideAll(),t.classList.remove("tab")});$("#suggestions__carousel").slick({autoplay:!1,dots:!1,infinite:!1,speed:100,slidesToShow:4,slidesToScroll:4,responsive:[{breakpoint:1024,settings:{slidesToShow:3,slidesToScroll:3}},{breakpoint:600,settings:{slidesToShow:2,slidesToScroll:2}},{breakpoint:480,settings:{slidesToShow:1,slidesToScroll:1}}]})});